import { useEffect } from 'react';
import { AdSegment } from '../utils/useVideoData';

interface TimelineMarkerProps {
  adSegments: AdSegment[];
}

export const TimelineMarker = ({ adSegments }: TimelineMarkerProps) => {
  useEffect(() => {
    const injectTimelineMarkers = () => {
      console.log('Attempting to inject markers...');

      // Wait for the YouTube player to be ready
      const progressBar = document.querySelector('.ytp-timed-markers-container');
      const chaptersContainer = document.querySelector('.ytp-chapters-container');

      console.log('Found elements:', { progressBar, chaptersContainer });

      if (!progressBar || !chaptersContainer) {
        console.log('Elements not found, retrying...');
        setTimeout(injectTimelineMarkers, 1000);
        return;
      }

      // Clear any existing markers
      const existingMarkers = document.querySelectorAll('.yt-adskip-marker');
      existingMarkers.forEach(marker => marker.remove());

      // Get video duration from the progress bar
      const duration = Number(progressBar.getAttribute('aria-valuemax')) || 0;
      console.log('Video duration:', duration);

      if (!duration) {
        console.log('No duration found, retrying...');
        setTimeout(injectTimelineMarkers, 1000);
        return;
      }

      // Create container for markers if it doesn't exist
      let markerContainer = document.querySelector('.yt-adskip-marker-container') as HTMLDivElement | null;
      if (!markerContainer) {
        console.log('Creating new marker container');
        markerContainer = document.createElement('div');
        markerContainer.className = 'yt-adskip-marker-container';
        Object.assign(markerContainer.style, {
          position: 'absolute',
          top: '0',
          left: '0',
          right: '0',
          bottom: '0',
          pointerEvents: 'none',
          zIndex: '1'
        });
        chaptersContainer.appendChild(markerContainer);
      }

      console.log('Creating markers for segments:', adSegments);

      // Create markers for each ad segment
      adSegments.forEach(segment => {
        if (!markerContainer) return;

        const marker = document.createElement('div');
        marker.className = 'yt-adskip-marker';

        // Calculate position and width
        const startPercent = (segment.start / duration) * 100;
        const endPercent = (segment.end / duration) * 100;
        const width = endPercent - startPercent;

        console.log('Creating marker:', { startPercent, endPercent, width });

        // Style the marker
        Object.assign(marker.style, {
          position: 'absolute',
          left: `${startPercent}%`,
          width: `${width}%`,
          top: '0',
          bottom: '0',
          backgroundColor: 'rgba(255, 0, 0, 0.5)',
          pointerEvents: 'none'
        });

        markerContainer.appendChild(marker);
      });

      // Verify markers were created
      const createdMarkers = document.querySelectorAll('.yt-adskip-marker');
      console.log('Created markers count:', createdMarkers.length);
    };

    const setupSkipButton = () => {
      // Remove any existing skip buttons
      const existingButton = document.querySelector('.yt-adskip-button');
      if (existingButton) existingButton.remove();

      // Create skip button (hidden by default)
      const skipButton = document.createElement('button');
      skipButton.className = 'yt-adskip-button ytp-button';
      skipButton.textContent = 'Skip Ad Section';
      skipButton.style.display = 'none';

      // Style the button
      Object.assign(skipButton.style, {
        position: 'absolute',
        bottom: '60px',
        right: '20px',
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        color: 'white',
        border: 'none',
        padding: '10px 20px',
        cursor: 'pointer',
        zIndex: '2147483647',
        borderRadius: '4px'
      });

      // Add to player
      const player = document.querySelector('.html5-video-player') as HTMLElement;
      if (player) player.appendChild(skipButton);

      // Monitor video time and show/hide skip button
      const video = document.querySelector('video') as HTMLVideoElement;
      if (!video) return;

      const checkAdSegment = () => {
        const currentTime = video.currentTime;
        const activeSegment = adSegments.find(
          segment => currentTime >= segment.start && currentTime < segment.end
        );

        if (activeSegment) {
          skipButton.style.display = 'block';
          skipButton.onclick = () => {
            video.currentTime = activeSegment.end;
            skipButton.style.display = 'none';
          };
        } else {
          skipButton.style.display = 'none';
        }
      };

      video.addEventListener('timeupdate', checkAdSegment);
    };

    // Initial setup with retry mechanism
    const setup = () => {
      console.log('Running setup...');
      injectTimelineMarkers();
      setupSkipButton();
    };
    setup();

    // Re-run setup when navigating to a new video
    const observer = new MutationObserver((mutations) => {
      for (const mutation of mutations) {
        const target = mutation.target;
        if (target.nodeName === 'VIDEO' || 
            (target instanceof Element && target.classList.contains('ytp-progress-bar'))) {
          console.log('Detected video change, re-running setup');
          setup();
          break;
        }
      }
    });

    observer.observe(document.body, {
      childList: true,
      subtree: true,
      attributes: true,
      attributeFilter: ['src', 'aria-valuemax']
    });

    return () => {
      observer.disconnect();
      const video = document.querySelector('video');
      if (video) {
        video.removeEventListener('timeupdate', () => {});
      }
    };
  }, [adSegments]);

  return null;
};
