import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import Alert from '@mui/material/Alert';
import { SearchResults } from './components/SearchResults';
import { SearchBar } from './components/SearchBar';
import { AdMarker } from './components/AdMarker';
import { TimelineMarker } from './components/TimelineMarker';
import { useVideoData } from './utils/useVideoData';
import { useThemeDetector } from './utils/useThemeDetector';

// Add global declaration for the adSkipObserver property
declare global {
  interface Window {
    adSkipObserver?: MutationObserver;
  }
}

const App: React.FC = () => {
  const [videoId, setVideoId] = useState<string>('');
  const [searchQuery, setSearchQuery] = useState<string>('');
  const isYouTubeDark = useThemeDetector();
  
  const theme = createTheme({
    palette: {
      mode: isYouTubeDark ? 'dark' : 'light',
      primary: {
        main: '#ff0000', // YouTube red
      },
      background: {
        default: isYouTubeDark ? '#0f0f0f' : '#ffffff',
        paper: isYouTubeDark ? '#272727' : '#ffffff',
      },
    },
  });

  const {
    data,
    loading,
    error,
    fetchData
  } = useVideoData();

  useEffect(() => {
    // Get current tab's URL to extract video ID
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      const url = tabs[0]?.url || '';
      const urlParams = new URLSearchParams(new URL(url).search);
      const id = urlParams.get('v');
      if (id) {
        setVideoId(id);
        fetchData(id);
      }
    });
  }, []);

  useEffect(() => {
    if (data?.adSegments && data.adSegments.length > 0) {
      chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
        const tabId = tabs[0]?.id;
        if (tabId) {
          // First inject the content script
          chrome.scripting.executeScript({
            target: { tabId },
            files: ['content.js']
          }).then(() => {
            // Then inject the markers script with the ad segments data
            chrome.scripting.executeScript({
              target: { tabId },
              func: (segments) => {
                // Function that will run in the context of the YouTube page
                const injectMarkers = () => {
                  // Target specifically the ytp-timed-markers-container
                  const markersContainer = document.querySelector('.ytp-timed-markers-container');
                  const progressBar = document.querySelector('.ytp-progress-bar');
                  
                  console.log('Target elements:', { markersContainer, progressBar });
                  
                  // If elements aren't found, retry after a short delay
                  if (!markersContainer || !progressBar) {
                    console.log('Elements not found, will retry in 1 second');
                    setTimeout(injectMarkers, 1000);
                    return;
                  }

                  // Clean up any existing markers
                  const existingMarkers = document.querySelectorAll('.yt-adskip-marker');
                  existingMarkers.forEach(marker => marker.remove());

                  // Get video duration from the progress bar
                  const duration = Number(progressBar.getAttribute('aria-valuemax')) || 0;
                  if (!duration) {
                    console.log('No duration found, will retry in 1 second');
                    setTimeout(injectMarkers, 1000);
                    return;
                  }

                  console.log('Adding markers for segments:', segments);

                  // Create each marker directly in the markers container
                  segments.forEach(segment => {
                    const marker = document.createElement('div');
                    marker.className = 'yt-adskip-marker';
                    
                    // Calculate position
                    const startPercent = (segment.start / duration) * 100;
                    const endPercent = (segment.end / duration) * 100;
                    const width = endPercent - startPercent;
                    
                    // Style the marker
                    Object.assign(marker.style, {
                      position: 'absolute',
                      left: `${startPercent}%`,
                      width: `${width}%`,
                      height: '100%',
                      backgroundColor: 'rgba(255, 0, 0, 0.5)',
                      pointerEvents: 'none'
                    });
                    
                    // Add directly to the markers container
                    markersContainer.appendChild(marker);
                    console.log('Added marker:', { start: segment.start, end: segment.end, width });
                  });
                };

                // Set up skip button
                const setupSkipButton = () => {
                  // Remove any existing skip buttons
                  const existingButton = document.querySelector('.yt-adskip-button');
                  if (existingButton) existingButton.remove();

                  // Create skip button (hidden by default)
                  const skipButton = document.createElement('button');
                  skipButton.className = 'yt-adskip-button ytp-button';
                  skipButton.textContent = 'Skip Ad Section';
                  skipButton.style.display = 'none';
                  
                  // Style the button
                  Object.assign(skipButton.style, {
                    position: 'absolute',
                    bottom: '60px',
                    right: '20px',
                    backgroundColor: 'rgba(0, 0, 0, 0.7)',
                    color: 'white',
                    border: 'none',
                    padding: '10px 20px',
                    cursor: 'pointer',
                    zIndex: '2147483647',
                    borderRadius: '4px'
                  });

                  // Add to player
                  const player = document.querySelector('.html5-video-player');
                  if (player) player.appendChild(skipButton);

                  // Monitor video time and show/hide skip button
                  const video = document.querySelector('video');
                  if (!video) return;

                  const checkAdSegment = () => {
                    const currentTime = video.currentTime;
                    const activeSegment = segments.find(
                      segment => currentTime >= segment.start && currentTime < segment.end
                    );

                    if (activeSegment) {
                      skipButton.style.display = 'block';
                      skipButton.onclick = () => {
                        video.currentTime = activeSegment.end;
                        skipButton.style.display = 'none';
                      };
                    } else {
                      skipButton.style.display = 'none';
                    }
                  };

                  video.addEventListener('timeupdate', checkAdSegment);
                };

                // Initial setup
                injectMarkers();
                setupSkipButton();

                // Set up a persistent observer to handle YouTube's dynamic UI
                if (!window.adSkipObserver) {
                  window.adSkipObserver = new MutationObserver(() => {
                    console.log('Player changed, re-injecting markers');
                    injectMarkers();
                  });
                  
                  window.adSkipObserver.observe(document.documentElement, {
                    childList: true,
                    subtree: true
                  });

                  console.log('Observer set up for ad markers');
                }
              },
              args: [data.adSegments]
            });
          });
        }
      });
    }
  }, [data?.adSegments]);

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleTimestampClick = (timestamp: number) => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      const tabId = tabs[0]?.id;
      if (tabId) {
        chrome.scripting.executeScript({
          target: { tabId },
          func: (time) => {
            const video = document.querySelector('video');
            if (video) {
              video.currentTime = time;
              video.play();
            }
          },
          args: [timestamp]
        });
      }
    });
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ 
        width: 400, 
        minHeight: 500,
        p: 2,
        display: 'flex',
        flexDirection: 'column',
        gap: 2
      }}>
        <SearchBar 
          onSearch={handleSearch}
          disabled={loading || !videoId}
        />
        
        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
            <CircularProgress />
          </Box>
        )}

        {error && (
          <Alert severity="error">
            {error}
          </Alert>
        )}

        {data && (
          <>
            {data.adSegments && data.adSegments.length > 0 && (
              <AdMarker 
                adSegments={data.adSegments}
                onTimestampClick={handleTimestampClick}
              />
            )}
            
            <SearchResults 
              transcript={data.transcript}
              searchQuery={searchQuery}
              onTimestampClick={handleTimestampClick}
            />
          </>
        )}
      </Box>
    </ThemeProvider>
  );
};

export default App;
